import { ref, onMounted } from 'vue';
import axios from 'axios';

// 定义响应式数据变量
const total = 0;

// 定义表格的样式及状态控制变量

export default {
  __name: 'OrderComponent',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const data = ref([]);
    const stripe = ref(true);
    const bordered = ref(true);
    const hover = ref(false);
    const tableLayout = ref(false);
    const size = ref('medium');
    const showHeader = ref(true);

    // 定义表格列配置
    const columns = ref([{
      colKey: 'oid',
      title: '订单号',
      width: '100'
    }, {
      colKey: 'order_time',
      title: '下单时间',
      width: '150'
    }, {
      colKey: 'cid',
      title: '顾客号',
      width: '100'
    }, {
      colKey: 'status',
      title: '订单状态',
      width: '100'
    },
    // { colKey:'detail', title: '订单详细', width: '100' },
    {
      colKey: 'total_price',
      title: '订单总价',
      width: '100'
    }
    // { colKey:'confirm', title: '确认订单', width: '100' },
    ]);

    // 定义行点击事件处理函数
    const handleRowClick = e => {
      console.log(e);
    };

    // 请求后端数据并写入表中
    const fetchData = async () => {
      try {
        console.log('开始请求数据...');
        const response = await axios.get('/api/orders'); // 根据实际API端点替换
        // const response = await axios.get('http://localhost:8080/orders');
        console.log('请求数据成功:', response.data);
        // console.log('请求数据总数:', response.data.length);
        data.value = response.data;
        total.value = response.data.length;
      } catch (error) {
        console.error('请求数据失败:', error);
      }
    };

    // 定义分页配置
    const pagination = {
      defaultCurrent: 1,
      defaultPageSize: 5,
      total
    };
    onMounted(() => {
      fetchData();
    });
    const __returned__ = {
      data,
      total,
      stripe,
      bordered,
      hover,
      tableLayout,
      size,
      showHeader,
      columns,
      handleRowClick,
      fetchData,
      pagination,
      ref,
      onMounted,
      get axios() {
        return axios;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
